import { Transaction, TransactionObjectArgument } from '@mysten/sui/transactions';
import { Exchange, QuoteQueryParams } from '../types';
export type NormalizedQuote<T = unknown> = {
    exchange: Exchange;
    raw: T;
    amountOut: bigint;
};
export type QuoteResult<T = unknown> = {
    exchange: Exchange;
    amountIn: string;
    tokenIn: string;
    route: T;
};
export interface ExchangeAdapter<T = unknown> {
    name(): string;
    init?(): Promise<void>;
    getRoutes(params: QuoteQueryParams): Promise<NormalizedQuote<T> | null>;
    buildTx(tx: Transaction, quote: QuoteResult<T>, sender: string, slippage: number, coinIn?: TransactionObjectArgument): Promise<{
        coinOut: TransactionObjectArgument;
        tx: Transaction;
    }>;
}
